INSERT INTO `catches_fish` VALUES (1,'Rainbow Trout','Black spotting over the length of the body, pink/red stripe along lateral line from gills to tail.','RNTR','rainbow-trout','fish/Rainbow_Trout.png'),(2,'Brown Trout','Large black spots on back and body, red spots (some with pale halos) on the sides.','BNTR','brown-trout','fish/Brown_Trout.png'),(3,'Tiger Trout','Vermiculation (worm-like markings) on body and dorsal fin. Lower fins have a pale leading edge. Markings may appear brownish or greenish depending on location.','TGTR','tiger-trout','fish/Tiger_Trout.png'),(5,'Cutthroat Trout','Black spots (without halos) on sides that are more concentrated on the back and tail, red/orange streak under the jaw, slightly forked tail.','CTTR','cutthroat-trout','fish/Cutthroat_Trout.png'),(6,'Brook Trout','<p><span style=\"color:#9b59b6\">Lower fins have a black stripe behind a pale leading edge, light colored spots on the body, black markings on dorsal fin, a slightly forked tail.</span></p>','BKTR','brook-trout','fish/Brook_Trout.png'),(7,'Walleye','Large eyes, two distinct fins present on back (first featuring large spines), dusky vertical bars on body with a yellow-olive back and white underside. Walleye usually have a distinctive white tip on the lower lobe of the tail.','WALL','walleye','fish/Walleye.png'),(8,'Northern Pike','','NRPK','northern-pike','fish/pike.jpeg'),(9,'Lake Whitefish','','LWF','lake-whitefish','fish/Lake_whitefish1.jpg'),(10,'Arctic Grayling','','ARTG','arctic-grayling','fish/ArcticGrayling.jpg'),(11,'Lake Trout','','LKTR','lake-trout','fish/Lake_Trout.jpg'),(12,'Bull Trout','','BLTR','bull-trout','fish/Bull_Trout.png'),(14,'Perch','','PRCH','perch','fish/yellow-perch_w9NqQrF.jpg');
